import React, { useState } from 'react';
import './profileCard.css'; // Apply styling for the card

function ProfileCard({ initialProfiles }) {
  const [profiles, setProfiles] = useState(initialProfiles);

  // Function to add a new card
  const addNewProfile = () => {
    // You can add a new profile object to the 'profiles' state here
    // For example:
    const newProfile = {
      user: {
        name: 'New User',
        avatar: 'url-to-avatar',
      },
      playlist: ['New Song 1', 'New Song 2', 'New Song 3'],
    };

    setProfiles([...profiles, newProfile]);
  };

  return (
    <div className="profile-card">
      {/* Map over profiles to display profile cards */}
      {profiles.map((profile, index) => (
        <div key={index}>
          {/* Your existing card code here */}
          {/* Display user's name, picture, vibe meter, playlist, and comment section */}
        </div>
      ))}

      {/* Button to add a new card */}
      <button onClick={addNewProfile}>Add New Card</button>
    </div>
  );
}

export default ProfileCard;
